import React, {useState} from 'react'
import Video from './video.mp4';
import { HeroContainer, HeroBg, VideoBg, HeroContent, HeroH1, HeroP, HeroBtnWrapper, ArrowForward, ArrowRight} from './HeroElements';
export const HeroSec = () => {


  const [hover, setHover] = useState(false)

  const onHover = () => {
    setHover(!hover)
  }

  return (
    <HeroContainer>
      <div className='HeroContainer'>
        <HeroBg>
        <div className='HeroBg'>
          <div className='VideoBg'>
            <VideoBg autoPlay loop muted src={Video} type='video/mp4' />
          </div>
        </div>
        </HeroBg>
        <HeroContent>
          <HeroH1>Revolutionizing The Way World Views Art</HeroH1>
          <HeroP>Find art works from the most popular artisits of this year.</HeroP>
          <HeroBtnWrapper>
            <Button to="Browse More" onMouseEnter={onHover} onMouseLeave={onHover}>
              Browse More {hover ? <ArrowForward /> : <ArrowRight/>}
            </Button>
          </HeroBtnWrapper>
        </HeroContent>
        </div>
    </HeroContainer>
  )
}
